import Phaser from 'phaser';
import Preload from './scenes/Preload';
import { Splash1 } from './scenes/Splash1';
import { Splash2 } from './scenes/Splash2';
import { Splash3 } from './scenes/Splash3';
import { MainMenu } from './scenes/MainMenu';
import { OptionsMenu } from './scenes/OptionsMenu';
import Game from './scenes/Game';
import GameOver from './scenes/GameOver';

// GBA resolution
const BASE_W = 240;
const BASE_H = 160;

export function createGame(parent: HTMLElement): Phaser.Game {
  const config: Phaser.Types.Core.GameConfig = {
    type: Phaser.AUTO,
    parent,
    width: BASE_W,
    height: BASE_H,
    pixelArt: true,
    roundPixels: true,
    antialias: false,
    render: {
      pixelArt: true,
      antialias: false,
      roundPixels: true
    },
    physics: {
      default: 'arcade',
      arcade: {
        gravity: { x: 0, y: 800 }, // Less floaty gravity
        debug: false
      }
    },
    scale: {
      mode: Phaser.Scale.FIT,
      autoCenter: Phaser.Scale.CENTER_BOTH,
      width: 240,
      height: 160,
      zoom: 1 // No additional zoom - show full scene
    },
    input: {
      keyboard: true,
      mouse: true,
      touch: true,
      gamepad: false,
      activePointers: 1
    },
    scene: [Preload, Splash1, Splash2, Splash3, MainMenu, OptionsMenu, Game, GameOver],
    backgroundColor: '#2c5f2d' // GBA green background
  };

  const game = new Phaser.Game(config);

  // Force disable text smoothing on the canvas context
  game.events.once('ready', () => {
    const canvas = game.canvas as HTMLCanvasElement;
    const ctx = canvas.getContext('2d');
    if (ctx) {
      (ctx as any).imageSmoothingEnabled = false;
      (ctx as any).webkitImageSmoothingEnabled = false;
      (ctx as any).mozImageSmoothingEnabled = false;
      (ctx as any).msImageSmoothingEnabled = false;
    }
  });

  return game;
}

client/src/hooks/use-is-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

